// controllers/AccountController.js
import db from '../config/db.js';

// 1. Trả về danh sách tài khoản
export const getAccounts = async (req, res) => {
    try {
        const [accounts] = await db.execute('SELECT * FROM users');
        res.json(accounts);
    } catch (error) {
        res.status(500).send('Lỗi khi lấy danh sách tài khoản');
    }
};

// 2. Trả về thông tin tài khoản cụ thể
export const getAccountById = async (req, res) => {
    const { id } = req.params;
    try {
        const [[account]] = await db.execute('SELECT * FROM users WHERE id = ?', [id]);
        if (account) {
            res.json(account);
        } else {
            res.status(404).send('Tài khoản không tồn tại');
        }
    } catch (error) {
        res.status(500).send('Lỗi khi lấy thông tin tài khoản');
    }
};

// 3. Tạo tài khoản
export const createAccount = async (req, res) => {
    const { username, password } = req.body;
    try {
        await db.execute('INSERT INTO users (username, password) VALUES (?, ?)', [username, password]);
        res.status(201).send('Tài khoản đã được tạo');
    } catch (error) {
        res.status(500).send('Lỗi khi tạo tài khoản');
    }
};

// 4. Sửa tài khoản
export const updateAccount = async (req, res) => {
    const { id } = req.params;
    const { username, password } = req.body;
    try {
        await db.execute('UPDATE users SET username = ?, password = ? WHERE id = ?', [username, password, id]);
        res.send('Tài khoản đã được cập nhật');
    } catch (error) {
        res.status(500).send('Lỗi khi cập nhật tài khoản');
    }
};

// 5. Xóa tài khoản
export const deleteAccount = async (req, res) => {
    const { id } = req.params;
    try {
        await db.execute('DELETE FROM users WHERE id = ?', [id]);
        res.send('Tài khoản đã được xóa');
    } catch (error) {
        res.status(500).send('Lỗi khi xóa tài khoản');
    }
};

// 6. Đăng nhập
export const login = async (req, res) => {
    const { username, password } = req.body;
    const user = await getUserByUsername(username);

    if (!user) return res.status(404).send("Sai tên tài khoản");
    if (user.password !== password) return res.status(401).send("Sai mật khẩu");

    req.session.username = username; // Lưu tên người dùng vào session
    res.send('Đăng nhập thành công');
};

// 7. Đăng xuất
export const logout = (req, res) => {
    req.session.destroy(err => {
        if (err) {
            return res.status(500).send("Không thể đăng xuất");
        }
        res.send('Đăng xuất thành công');
    });
};

const getUserByUsername = async (username) => {
    const [[user]] = await db.execute('SELECT * FROM users WHERE username = ?', [username]);
    return user;
};

