import express from 'express';
import { getUsers, getUserById, updateUser, deleteUser, createUser } from '../controllers/UserController.js';

const router = express.Router();

// Route để hiển thị danh sách người dùng
router.get('/users', getUsers);

// Route để hiển thị chi tiết người dùng
router.get('/users/:id', getUserById);

// Route để cập nhật thông tin người dùng
router.post('/users/update/:id', updateUser);

// Route để xóa người dùng
router.post('/users/delete/:id', deleteUser);

// Route để thêm người dùng mới
router.post('/users/create', createUser);

// Export router
export default router;

